import React, { Component } from 'react';
import './App.css'
import Header from './Components/Header/Header'
import Filter from './Components/Filter/Filter'
import List from './Components/List/List'
export default class App extends Component {

	state = {todos:[]}
//添加一个todo，接受todo对象
	addTodo = (todoObj)=>{
		const{todos} = this.state//获取原todos
		const newTodos = [todoObj,...todos]//追加todo
		this.setState({todos:newTodos})//更新
	}
//更新todo勾选状态
	changeTodo = (id,done)=>{
		const{todos}=this.state
		const newTodos = todos.map((todoObj)=>{
			if(todoObj.id===id)
			return{...todoObj,done}
			else return todoObj
		})
		this.setState({todos:newTodos})
	}
//删除一个todo
	deleteTodo = (id)=>{
		const{todos}=this.state
		const newTodos = todos.filter((todoObj) => {
			return todoObj.id!==id
		})
		this.setState({todos:newTodos})
	}

	viewAll = ()=>{
		const{todos}=this.state
		const newTodos = {todos}
		this.setState({todos:newTodos})
	}
	viewActive = (done)=>{
		const{todos}=this.state
		const newTodos = todos.filter((todoObj) => {
			return todoObj.done===false
		 })
		 this.setState({todos:newTodos})
	}


  render() {
	const {todos} = this.state
    return (
    <div className='Todo'>
		<section className="todoapp">
           	<Header addTodo={this.addTodo}/>
			<List todos={todos} changeTodo={this.changeTodo} deleteTodo={this.deleteTodo}/>
			<Filter todos={todos} viewAll={this.viewAll} viewActive={viewActive}/>
		</section>
    </div>
	)
	}
}
