import React, { Component } from 'react';

export default class Filter extends Component {
  render() {
      const {todos} = this.props
      const doneCount = todos.reduce((pre,todo)=>{return pre-(todo.done?1:0)},todos.length)

    return (

	    <footer className="footer" style={{display:(todos.length?'block':'none')}}>
        <span className="todo-count" style={{display:(todos.length?'block':'none')}}>{doneCount}item is left</span>

                <ul className="filters">
                    <li>
                        <a href="#/" className="selected">All</a>
                    </li>
                    <li>
                        <a href="#/active">Active</a>
                    </li>
                    <li>
                        <a href="#/completed">Completed</a>
                    </li>
                </ul>

                <button className="clear-completed">Clear completed</button>
        </footer>
            )
    }
}
