import React, { Component } from 'react';

export default class Filter extends Component {
    all=React.createRef()
    active=React.createRef()
    completed=React.createRef()

    handleAll = ()=>{
        this.all.current.className="selected"
        this.active.current.className=""
        this.completed.current.className=""
        this.props.viewAll()
    }
    handleActive = (done)=>{
        this.all.current.className=""
        this.active.current.className="selected"
        this.completed.current.className=""
        this.props.viewActive(done)
    }
    /* handleCompleted = ()=>{
        this.all.current.className=""
        this.active.current.className=""
        this.completed.current.className="selected"
        this.props.viewCompleted()
    } */
  render() {
      const {todos} = this.props
      const {done} = this.props
      const undoneCount = todos.reduce((pre,todo)=>{return pre-(todo.done?1:0)},todos.length)

    return (

	    <footer className="footer" style={{display:(todos.length?'block':'none')}}>
        <span className="todo-count" style={{display:(todos.length?'block':'none')}}>{undoneCount}item left</span>

                <ul className="filters">
                    <li>
                        <a ref = {this.all} href="#/" className="selected" onClick={this.handleAll}>All</a>
                    </li>
                    <li>
                        <a ref = {this.active} onClick = {this.handleActive(done)}href="#/active">Active</a>
                    </li>
                    <li>
                        <a href="#/completed">Completed</a>
                    </li>
                </ul>

                <button className="clear-completed">Clear completed</button>
        </footer>
            )
    }
}
